insert into reports (id, text) values ('b0d6cba7-20ba-49b2-a8f3-a0f86575aff2', 'Заявка подавалась в конце марта, в начале апреля 2022г. У вас "отлично" построен сервис-банк для бизнеса, где не сохраняются данные и мы не можем предоставить доказательства, а вы рады этому.');
insert into reports (id, text) values ('5c41144a-b073-45cd-966d-48c19cb35ca9', 'Добрый день! Мы заказывали справку об оборотах за период с 01.01.2022 по 31.03.2022, а вы прислали выписку по счету... Просим предоставить запрошенный нами документ.');
insert into reports (id, text) values ('14404982-9be4-4646-8a75-2fc4b57be0f0', 'Добрый день. Просьба указать основание, правовую норму, статью закона, постановление, на основании которого Дополнительным соглашением №К1/54-01/21-12345-Д01 от 22.11.2021 была проведена реструктуризация кредитного договора. Спасибо');
insert into reports (id, text) values ('b49b2fad-5487-4b65-8544-431dba9a9f65', 'Добрый день. Вопрос: где на данной выписке УКЭП (усиленная квалифицированная электронная подпись), которую я заказывал? Это обычная выписка без всяких подписей. За что взяли оплату, если такую выписку я могу получить самостоятельно без вашей помощи?');
